package main;

import java.util.HashMap;
import java.util.Scanner;

public class Kraken {
		
		public static void main(String[] args) {
			// TODO Auto-generated method stub
			krakenCount(12,12);
		}
		static HashMap<String,Integer> nodeValues = new HashMap<>();
		static int m,n,count=0,i=0,j=0;
		static boolean flag=false;
		public static void krakenCount(int row, int column) {
			// TODO Auto-generated method stub
			m = row;
			n = column;
			call();
			System.out.println(count);
		}

		public static void call() {
			// TODO Auto-generated method stub
			if(i==m-1 && j==n-1){
				nodeValues.put(i+"+"+j,1);
				count++;
			}
			else {
				if(!nodeValues.containsKey(i+"+"+j)){
					nodeValues.put(i+"+"+j,0);
					if(j+1<n){
						++j;
						call();
						--j;
						nodeValues.put(i+"+"+j, nodeValues.get(i+"+"+j)+nodeValues.get((i)+"+"+(j+1)) );
					}
					if(i+1<m && j+1<n){
						++j;
						++i;
						call();
						--j;
						--i;
						nodeValues.put(i+"+"+j, nodeValues.get(i+"+"+j)+nodeValues.get((i+1)+"+"+(j+1)) );
					}
					if(i+1<m){
						++i;
						call();
						--i;
						nodeValues.put(i+"+"+j, nodeValues.get(i+"+"+j)+nodeValues.get((i+1)+"+"+j) );
						
					}
				}else{
					count += nodeValues.get(i+"+"+j);
				}
				}
		}

	}

